controller.player4.onButtonEvent(ControllerButton.A, ControllerButtonEvent.Pressed, function () {
    if (bloodthirst == true) {
        Rushdowner.sayText("I HAVE YOU NOW!", 1500, false)
        controller.player4.moveSprite(Rushdowner, 200, 200)
        bloodthirst = false
        timer.after(1500, function () {
            controller.player4.moveSprite(Rushdowner, 0, 0)
            Rushdowner.sayText("better run.", 2000, false)
        })
        timer.after(3500, function () {
            controller.player4.moveSprite(Rushdowner, 75, 75)
        })
    }
    timer.after(40000, function () {
        bloodthirst = true
        Rushdowner.sayText("my thirst returns.", 2000, false)
    })
})
controller.player1.onButtonEvent(ControllerButton.A, ControllerButtonEvent.Pressed, function () {
    if (healpot == true) {
        Healer.sayText("Don't Die Yet.", 2000, true)
        info.player1.changeLifeBy(25)
        info.player2.changeLifeBy(25)
        info.player3.changeLifeBy(25)
        healpot = false
        timer.after(50000, function () {
            healpot = true
            Healer.sayText("my potions are rebrewed!", 2000, true)
        })
    }
})
